import React from 'react';
import { useCart } from '../contexts/CartContext';
import { useRouter } from '../contexts/RouterContext';

const CartPage = () => {
  const { cart, removeFromCart, clearCart } = useCart();
  const { navigate } = useRouter();

  const total = cart.reduce((sum, item) => sum + item.price, 0);

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-4">Carrinho</h1>
      {cart.length === 0 ? (
        <p>Seu carrinho est√° vazio.</p>
      ) : (
        <div>
          {cart.map((item, index) => (
            <div key={index} className="flex justify-between items-center mb-2 border-b p-2">
              <div>
                <h2 className="font-semibold">{item.name}</h2>
                <p>R$ {item.price.toFixed(2)}</p>
              </div>
              <button
                onClick={() => removeFromCart(item.id)}
                className="bg-red-500 hover:bg-red-400 text-white px-3 py-1 rounded"
              >
                Remover
              </button>
            </div>
          ))}
          <p className="text-xl font-bold mt-4">Total: R$ {total.toFixed(2)}</p>
          <div className="mt-4 flex gap-2">
            <button
              onClick={clearCart}
              className="bg-gray-500 hover:bg-gray-400 text-white px-4 py-2 rounded"
            >
              Limpar Carrinho
            </button>
            <button
              onClick={() => navigate('/checkout')}
              className="bg-blue-500 hover:bg-blue-400 text-white px-4 py-2 rounded"
            >
              Finalizar Compra
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default CartPage;
