import React, { useEffect, useState } from 'react';
import { useRouter } from '../contexts/RouterContext';

const AdminPage = () => {
  const [products, setProducts] = useState([]);
  const { navigate } = useRouter();

  useEffect(() => {
    fetch('http://localhost:3001/api/products')
      .then(res => res.json())
      .then(data => setProducts(data))
      .catch(err => console.error(err));
  }, []);

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-4">Painel Admin - Produtos</h1>
      <button
        onClick={() => navigate('/admin/add-product')}
        className="bg-green-500 hover:bg-green-400 text-white px-4 py-2 rounded mb-4"
      >
        Adicionar Produto
      </button>
      <div className="flex flex-col gap-2">
        {products.map(product => (
          <div key={product.id} className="border p-2 flex justify-between items-center rounded">
            <span>{product.name}</span>
            <div className="flex gap-2">
              <button
                onClick={() => navigate('/admin/edit-product', { id: product.id })}
                className="bg-yellow-500 hover:bg-yellow-400 text-white px-3 py-1 rounded"
              >
                Editar
              </button>
              <button
                onClick={() => {
                  fetch(`http://localhost:3001/api/products/${product.id}`, { method: 'DELETE' })
                    .then(() => setProducts(products.filter(p => p.id !== product.id)))
                    .catch(err => console.error(err));
                }}
                className="bg-red-500 hover:bg-red-400 text-white px-3 py-1 rounded"
              >
                Excluir
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AdminPage;
