import React, { useEffect, useState } from 'react';
import { useRouter } from '../contexts/RouterContext';
import { useCart } from '../contexts/CartContext';

const ProductPage = () => {
  const { params } = useRouter();
  const { addToCart } = useCart();
  const [product, setProduct] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:3001/api/products/${params.id}`)
      .then(res => res.json())
      .then(data => setProduct(data))
      .catch(err => console.error(err));
  }, [params.id]);

  if (!product) return <p className="p-4">Carregando...</p>;

  return (
    <div className="container mx-auto p-4 flex flex-col md:flex-row gap-4">
      <img src={product.image} alt={product.name} className="w-full md:w-1/2 h-auto object-cover"/>
      <div className="flex-1">
        <h1 className="text-3xl font-bold mb-2">{product.name}</h1>
        <p className="text-gray-700 mb-2">{product.description}</p>
        <p className="text-xl font-semibold mb-4">R$ {product.price.toFixed(2)}</p>
        <button
          onClick={() => addToCart(product)}
          className="bg-blue-500 hover:bg-blue-400 text-white px-4 py-2 rounded"
        >
          Adicionar ao Carrinho
        </button>
      </div>
    </div>
  );
};

export default ProductPage;
