import React, { useState, useEffect } from 'react';
import { Plus, Edit2, Trash2, Package, Truck } from 'lucide-react';
import { useAuth } from '../contexts/AuthContext';
import Loading from '../components/Loading';

const API_BASE = 'http://localhost:3001/api';

const AdminPage = () => {
  const [products, setProducts] = useState([]);
  const [suppliers, setSuppliers] = useState([]);

  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [editingProduct, setEditingProduct] = useState(null);
  const [formSubmitting, setFormSubmitting] = useState(false);
  const { token } = useAuth();

  const [formData, setFormData] = useState({
    name: '',
    description: '',
    price: '',
    stock: '',
    category: ''
  });

  // -------------------- FORNECEDORES --------------------
  const [supplierForm, setSupplierForm] = useState({
    name: '',
    contact: '',
    email: '',
    phone: ''
  });
  const [editingSupplier, setEditingSupplier] = useState(null);
  const [showSupplierForm, setShowSupplierForm] = useState(false);

  useEffect(() => {
    loadProducts();
    loadSuppliers();
  }, []);

  // -------------------- PRODUTOS --------------------
  const loadProducts = async () => {
    try {
      const response = await fetch(`${API_BASE}/products`);
      const data = await response.json();
      setProducts(data);
    } catch (error) {
      console.error('Erro ao carregar produtos:', error);
    } finally {
      setLoading(false);
    }
  };

  const resetForm = () => {
    setFormData({
      name: '',
      description: '',
      price: '',
      stock: '',
      category: ''
    });
    setEditingProduct(null);
    setShowForm(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setFormSubmitting(true);

    try {
      const data = new FormData();
      data.append('name', formData.name);
      data.append('description', formData.description);
      data.append('price', formData.price);
      data.append('stock', formData.stock);
      data.append('category', formData.category);

      const fileInput = document.getElementById('image');
      if (fileInput?.files[0]) {
        data.append('image', fileInput.files[0]);
      }

      const url = editingProduct
        ? `${API_BASE}/products/${editingProduct.id}`
        : `${API_BASE}/products`;

      const method = editingProduct ? 'PUT' : 'POST';

      const response = await fetch(url, {
        method,
        headers: { Authorization: `Bearer ${token}` },
        body: data
      });

      if (response.ok) {
        alert(editingProduct ? 'Produto atualizado!' : 'Produto criado!');
        resetForm();
        await loadProducts();
      } else {
        const errorData = await response.json();
        alert(`Erro: ${errorData.error || 'Erro ao salvar produto'}`);
      }
    } catch (error) {
      console.error('Erro ao salvar produto:', error);
    } finally {
      setFormSubmitting(false);
    }
  };

  const handleEdit = (product, e) => {
    e.preventDefault();
    e.stopPropagation();

    setEditingProduct(product);
    setFormData({
      name: product.name || '',
      description: product.description || '',
      price: product.price?.toString() || '',
      stock: product.stock?.toString() || '',
      category: product.category || ''
    });
    setShowForm(true);
  };

  const handleDelete = async (id, e) => {
    e.preventDefault();
    e.stopPropagation();

    if (!window.confirm('Excluir produto?')) return;

    try {
      const response = await fetch(`${API_BASE}/products/${id}`, {
        method: 'DELETE',
        headers: { Authorization: `Bearer ${token}` }
      });

      if (response.ok) {
        alert('Produto deletado!');
        await loadProducts();
      }
    } catch (error) {
      console.error('Erro ao deletar produto:', error);
    }
  };

  // -------------------- FORNECEDORES --------------------

  const loadSuppliers = async () => {
    try {
      const res = await fetch(`${API_BASE}/suppliers`);
      const data = await res.json();

      if (Array.isArray(data)) {
        setSuppliers(data);
      } else {
        console.error('Resposta inesperada:', data);
        setSuppliers([]);
      }
    } catch (err) {
      console.error('Erro ao carregar fornecedores:', err);
      setSuppliers([]);
    }
  };

  const handleSupplierSubmit = async (e) => {
    e.preventDefault();

    const method = editingSupplier ? 'PUT' : 'POST';
    const url = editingSupplier
      ? `${API_BASE}/suppliers/${editingSupplier.id}`
      : `${API_BASE}/suppliers`;

    try {
      const res = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(supplierForm),
      });

      if (res.ok) {
        alert(editingSupplier ? 'Fornecedor atualizado!' : 'Fornecedor criado!');
        setSupplierForm({ name: '', contact: '', email: '', phone: '' });
        setEditingSupplier(null);
        setShowSupplierForm(false);
        loadSuppliers();
      } else {
        alert('Erro ao salvar fornecedor');
      }
    } catch (error) {
      console.error('Erro ao salvar fornecedor:', error);
    }
  };

  const handleSupplierDelete = async (id) => {
    if (!window.confirm('Excluir fornecedor?')) return;

    await fetch(`${API_BASE}/suppliers/${id}`, {
      method: 'DELETE',
      headers: { Authorization: `Bearer ${token}` },
    });

    loadSuppliers();
  };

  if (loading) return <Loading />;

  return (
    <div className="container mx-auto px-4 py-8">

      {/* ------------------------------------------------ */}
      {/*                PRODUTOS                         */}
      {/* ------------------------------------------------ */}

      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">Painel Admin - Produtos</h1>
        <button
          onClick={handleNewProduct}
          className="bg-green-600 text-white px-6 py-3 rounded-lg hover:bg-green-700 flex items-center font-semibold"
        >
          <Plus className="w-5 h-5 mr-2" />
          Adicionar Produto
        </button>
      </div>

      {/* Formulário de Produto */}
      {showForm && (
        <div className="bg-white p-6 rounded-lg shadow-lg mb-6 border">
          <h2 className="text-xl font-bold mb-4">
            {editingProduct ? 'Editar Produto' : 'Novo Produto'}
          </h2>
          {/* mantém TODO O FORMULÁRIO DO PRODUTO EXATAMENTE COMO ESTÁ */}
          {/* (não removi nada, está igual ao seu) */}
          {/* ---------------- FORM PRODUTO ---------------- */}
          <form onSubmit={handleSubmit} className="space-y-4">
            {/* CAMPOS IGUAIS AO SEU */}
            {/* (código omitido aqui só para encurtar a resposta, mas MANTENHA o seu) */}
          </form>
        </div>
      )}

      {/* Lista de Produtos */}
      <div className="bg-white rounded-lg shadow mb-12">
        <div className="p-4 border-b">
          <h3 className="text-lg font-semibold">Lista de Produtos ({products.length})</h3>
        </div>
        <div className="divide-y divide-gray-200">
          {/* mantém sua lista exatamente igual */}
        </div>
      </div>

      {/* ------------------------------------------------ */}
      {/*                FORNECEDORES                     */}
      {/* ------------------------------------------------ */}
      
      <div className="flex justify-between items-center mt-12 mb-6">
        <h1 className="text-3xl font-bold flex items-center gap-2">
          <Truck className="w-7 h-7 text-blue-600" />
          Gerenciamento de Fornecedores
        </h1>

        <button
          onClick={() => { setShowSupplierForm(true); setEditingSupplier(null); }}
          className="bg-blue-600 text-white px-6 py-3 rounded-lg hover:bg-blue-700 flex items-center font-semibold"
        >
          <Plus className="w-5 h-5 mr-2" />
          Novo Fornecedor
        </button>
      </div>

      {/* Formulário de Fornecedor */}
      {showSupplierForm && (
        <form onSubmit={handleSupplierSubmit} className="bg-white p-6 rounded-lg shadow mb-8 space-y-4">
          <input
            type="text"
            placeholder="Nome do fornecedor"
            className="border rounded px-3 py-2 w-full"
            value={supplierForm.name}
            onChange={(e) => setSupplierForm({ ...supplierForm, name: e.target.value })}
            required
          />
          <input
            type="text"
            placeholder="Pessoa de contato"
            className="border rounded px-3 py-2 w-full"
            value={supplierForm.contact}
            onChange={(e) => setSupplierForm({ ...supplierForm, contact: e.target.value })}
          />
          <input
            type="email"
            placeholder="Email"
            className="border rounded px-3 py-2 w-full"
            value={supplierForm.email}
            onChange={(e) => setSupplierForm({ ...supplierForm, email: e.target.value })}
          />
          <input
            type="text"
            placeholder="Telefone"
            className="border rounded px-3 py-2 w-full"
            value={supplierForm.phone}
            onChange={(e) => setSupplierForm({ ...supplierForm, phone: e.target.value })}
          />

          <div className="flex gap-4">
            <button type="submit" className="bg-green-600 text-white px-6 py-2 rounded hover:bg-green-700">
              {editingSupplier ? 'Atualizar' : 'Salvar'}
            </button>
            <button
              type="button"
              onClick={() => setShowSupplierForm(false)}
              className="bg-gray-500 text-white px-6 py-2 rounded hover:bg-gray-600"
            >
              Cancelar
            </button>
          </div>
        </form>
      )}

      {/* Lista de Fornecedores */}
      <div className="bg-white rounded-lg shadow">
        <div className="p-4 border-b">
          <h3 className="text-lg font-semibold">Lista de Fornecedores ({suppliers.length})</h3>
        </div>

        <div className="divide-y divide-gray-200">
          {suppliers.length === 0 ? (
            <div className="p-8 text-center text-gray-500">
              <Truck className="w-16 h-16 mx-auto mb-4 text-gray-300" />
              <p>Nenhum fornecedor cadastrado ainda.</p>
              <p className="text-sm">Clique em "Novo Fornecedor" para adicionar.</p>
            </div>
          ) : (
            suppliers.map((s) => (
              <div key={s.id} className="p-4 hover:bg-gray-50 flex justify-between items-center">
                <div>
                  <h3 className="font-semibold text-lg">{s.name}</h3>
                  <p className="text-sm text-gray-600">
                    {s.contact && <>Contato: {s.contact} · </>}
                    {s.email && <>Email: {s.email} · </>}
                    {s.phone && <>Telefone: {s.phone}</>}
                  </p>
                </div>

                <div className="flex gap-2">
                  <button
                    onClick={() => {
                      setEditingSupplier(s);
                      setSupplierForm(s);
                      setShowSupplierForm(true);
                    }}
                    className="bg-yellow-500 text-white p-2 rounded hover:bg-yellow-600"
                  >
                    <Edit2 className="w-4 h-4" />
                  </button>
                  <button
                    onClick={() => handleSupplierDelete(s.id)}
                    className="bg-red-500 text-white p-2 rounded hover:bg-red-600"
                  >
                    <Trash2 className="w-4 h-4" />
                  </button>
                </div>
              </div>
            ))
          )}
        </div>
      </div>

    </div>
  );
};

export default AdminPage;
