// src/contexts/AuthContext.jsx
import React, { createContext, useContext, useState, useEffect } from 'react';
import { API_BASE } from '../config/api';
import { useNavigate } from 'react-router-dom';

export const AuthContext = createContext();

export function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [token, setToken] = useState(localStorage.getItem('token'));

  useEffect(() => {
    const verificarToken = async () => {
      const storedToken = localStorage.getItem('token');
      
      if (storedToken) {
        try {
          const response = await fetch(`${API_BASE}/users/auth/verify`, {
            headers: { Authorization: `Bearer ${storedToken}` }
          });

          if (response.ok) {
            const data = await response.json();
            if (data.success && data.user) {
              setUser(data.user);
              setToken(storedToken);
              setLoading(false);
              return;
            }
          }
          
          // Se chegou aqui, o token é inválido
          console.warn('Token inválido, limpando localStorage...');
          localStorage.removeItem('token');
          setToken(null);
          setUser(null);
          
        } catch (error) {
          console.error('Erro ao verificar token:', error);
          localStorage.removeItem('token');
          setToken(null);
          setUser(null);
        }
      }
      
      setLoading(false);
    };

    verificarToken();
  }, []);

  const login = async (email, senha) => {
    try {
      const response = await fetch(`${API_BASE}/users/login`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, senha })
      });

      const data = await response.json();
      
      if (response.ok && data.success) {
        setUser(data.user);
        setToken(data.token);
        localStorage.setItem('token', data.token);
        return { success: true };
      }
      
      return { 
        success: false, 
        error: data.message || data.error || 'Erro ao fazer login' 
      };
    } catch (error) {
      console.error('Erro no login:', error);
      return { 
        success: false, 
        error: 'Erro de conexão com o servidor' 
      };
    }
  };

  const register = async (nome, email, senha) => {
    try {
      const response = await fetch(`${API_BASE}/users/register`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ nome, email, senha })
      });

      const data = await response.json();
      
      if (response.ok && data.success) {
        setUser(data.user);
        setToken(data.token);
        localStorage.setItem('token', data.token);
        return { success: true };
      }
      
      return { 
        success: false, 
        error: data.message || data.error || 'Erro ao registrar' 
      };
    } catch (error) {
      console.error('Erro no registro:', error);
      return { 
        success: false, 
        error: 'Erro de conexão com o servidor' 
      };
    }
  };

  const logout = () => {
    setUser(null);
    setToken(null);
    localStorage.removeItem('token');
  };

  return (
    <AuthContext.Provider value={{ 
      user, 
      login, 
      register, 
      logout, 
      loading, 
      token,
      isAuthenticated: !!user,
      isAdmin: user?.tipo_usuario === 'admin',
      isClient: user?.tipo_usuario === 'cliente'
    }}>
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth deve ser usado dentro de AuthProvider');
  }
  return context;
}
